package com.mycompany.myapp.rest;

import static org.springframework.http.MediaType.APPLICATION_JSON_VALUE;
import static org.springframework.web.bind.annotation.RequestMethod.DELETE;
import static org.springframework.web.bind.annotation.RequestMethod.GET;
import static org.springframework.web.bind.annotation.RequestMethod.POST;
import static org.springframework.web.bind.annotation.RequestMethod.PUT;

import java.net.URI;
import java.net.URISyntaxException;
import java.util.List;
import java.util.Optional;

import javax.inject.Inject;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.http.HttpHeaders;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;
import org.springframework.web.bind.annotation.RequestBody;

import com.mycompany.myapp.domain.Goods;
import com.mycompany.myapp.dto.GoodsDTO;
import com.mycompany.myapp.dto.GoodsDTOService;
import com.mycompany.myapp.dto.support.PageRequestByExample;
import com.mycompany.myapp.dto.support.PageResponse;
import com.mycompany.myapp.repository.GoodsRepository;
import com.mycompany.myapp.rest.support.AutoCompleteQuery;

@RestController
@RequestMapping("/api/goodss")
public class GoodsResource {

    private final Logger log = LoggerFactory.getLogger(GoodsResource.class);

    @Inject
    private GoodsRepository goodsRepository;
    @Inject
    private GoodsDTOService goodsDTOService;

    /**
     * Create a new Goods.
     */
    @RequestMapping(value = "/", method = POST, produces = APPLICATION_JSON_VALUE)
    public ResponseEntity<GoodsDTO> create(@RequestBody GoodsDTO goodsDTO) throws URISyntaxException {

        log.debug("Create GoodsDTO : {}", goodsDTO);

        if (goodsDTO.isIdSet()) {
            return ResponseEntity.badRequest().header("Failure", "Cannot create Goods with existing ID").body(null);
        }

        GoodsDTO result = goodsDTOService.save(goodsDTO);

        return ResponseEntity.created(new URI("/api/goodss/" + result.id)).body(result);
    }

    /**
    * Find by id Goods.
    */
    @RequestMapping(value = "/{id}", method = GET, produces = APPLICATION_JSON_VALUE)
    public ResponseEntity<GoodsDTO> findById(@PathVariable Integer id) throws URISyntaxException {

        log.debug("Find by id Goods : {}", id);

        return Optional.ofNullable(goodsDTOService.findOne(id)).map(goodsDTO -> new ResponseEntity<>(goodsDTO, HttpStatus.OK))
                .orElse(new ResponseEntity<>(HttpStatus.NOT_FOUND));
    }

    /**
     * Update Goods.
     */
    @RequestMapping(value = "/", method = PUT, produces = APPLICATION_JSON_VALUE)
    public ResponseEntity<GoodsDTO> update(@RequestBody GoodsDTO goodsDTO) throws URISyntaxException {

        log.debug("Update GoodsDTO : {}", goodsDTO);

        if (!goodsDTO.isIdSet()) {
            return create(goodsDTO);
        }

        GoodsDTO result = goodsDTOService.save(goodsDTO);

        return ResponseEntity.ok().body(result);
    }

    /**
     * Find a Page of Goods using query by example.
     */
    @RequestMapping(value = "/page", method = POST, produces = APPLICATION_JSON_VALUE)
    public ResponseEntity<PageResponse<GoodsDTO>> findAll(@RequestBody PageRequestByExample<GoodsDTO> prbe) throws URISyntaxException {
        PageResponse<GoodsDTO> pageResponse = goodsDTOService.findAll(prbe);
        return new ResponseEntity<>(pageResponse, new HttpHeaders(), HttpStatus.OK);
    }

    /**
    * Auto complete support.
    */
    @RequestMapping(value = "/complete", method = POST, produces = APPLICATION_JSON_VALUE)
    public ResponseEntity<List<GoodsDTO>> complete(@RequestBody AutoCompleteQuery acq) throws URISyntaxException {

        List<GoodsDTO> results = goodsDTOService.complete(acq.query, acq.maxResults);

        return new ResponseEntity<>(results, new HttpHeaders(), HttpStatus.OK);
    }

    /**
     * Delete by id Goods.
     */
    @RequestMapping(value = "/{id}", method = DELETE, produces = APPLICATION_JSON_VALUE)
    public ResponseEntity<Void> delete(@PathVariable Integer id) throws URISyntaxException {

        log.debug("Delete by id Goods : {}", id);

        try {
            goodsRepository.delete(id);
            return ResponseEntity.ok().build();
        } catch (Exception x) {
            // todo: dig exception, most likely org.hibernate.exception.ConstraintViolationException
            return ResponseEntity.status(HttpStatus.CONFLICT).build();
        }
    }
}