import { Injectable } from '@angular/core';
import { HttpClient, HttpResponse, HttpErrorResponse } from '@angular/common/http';
import { LazyLoadEvent } from 'primeng/primeng';
import { MessageService } from '../../service/message.service';
import { PageResponse, PageRequestByExample } from '../../support/paging';
import { Rate } from './rate';
import { Observable } from 'rxjs/Rx';
import { catchError, map } from 'rxjs/operators';
import 'rxjs/add/observable/throw';

@Injectable()
export class RateService {

    constructor(private http: HttpClient, private messageService : MessageService) {}

    /**
     * Get a Rate by id.
     */
    getRate(id : any) : Observable<Rate> {
        return this.http.get('/api/rates/' + id)
            .pipe(
                map(response => new Rate(response)),
                catchError(this.handleError)
            );
    }

    /**
     * Update the passed rate.
     */
    update(rate : Rate) : Observable<Rate> {
        let body = rate;

        return this.http.put('/api/rates/', body)
            .pipe(
                map(response => new Rate(response)),
                catchError(this.handleError)
            );
    }

    /**
     * Load a page (for paginated datatable) of Rate using the passed
     * rate as an example for the search by example facility.
     */
    getPage(rate : Rate, event : LazyLoadEvent) : Observable<PageResponse<Rate>> {
        let req = new PageRequestByExample(rate, event);
        let body = req;

        return this.http.post<PageResponse<any>>('/api/rates/page', body)
            .pipe(
                map(pr =>  new PageResponse<Rate>(pr.totalPages, pr.totalElements, Rate.toArray(pr.content))),
                catchError(this.handleError)
            );
    }

    /**
     * Performs a search by example on 1 attribute (defined on server side) and returns at most 10 results.
     * Used by RateCompleteComponent.
     */
    complete(query : string) : Observable<Rate[]> {
        let body = {'query': query, 'maxResults': 10};
        return this.http.post<any[]>('/api/rates/complete', body)
            .pipe(
                map(response => Rate.toArray(response)),
                catchError(this.handleError)
            );
    }

    /**
     * Delete an Rate by id.
     */
    delete(id : any) {
        return this.http.delete('/api/rates/' + id)
            .pipe(catchError(this.handleError));
    }

    // sample method from angular doc
    private handleError (error: HttpErrorResponse) {
        // TODO: seems we cannot use messageService from here...
        let errMsg = (error.message) ? error.message : 'Server error';
        console.error(errMsg);
        if (error.status === 401 ) {
            window.location.href = '/';
        }
        return Observable.throw(errMsg);
    }
}
