import { Injectable } from '@angular/core';
import { HttpClient, HttpResponse, HttpErrorResponse } from '@angular/common/http';
import { LazyLoadEvent } from 'primeng/primeng';
import { MessageService } from '../../service/message.service';
import { PageResponse, PageRequestByExample } from '../../support/paging';
import { WarehouseGoods } from './warehouseGoods';
import { Observable } from 'rxjs/Rx';
import { catchError, map } from 'rxjs/operators';
import 'rxjs/add/observable/throw';

@Injectable()
export class WarehouseGoodsService {

    constructor(private http: HttpClient, private messageService : MessageService) {}

    /**
     * Get a WarehouseGoods by id.
     */
    getWarehouseGoods(id : any) : Observable<WarehouseGoods> {
        return this.http.get('/api/warehouseGoodss/' + id)
            .pipe(
                map(response => new WarehouseGoods(response)),
                catchError(this.handleError)
            );
    }

    /**
     * Update the passed warehouseGoods.
     */
    update(warehouseGoods : WarehouseGoods) : Observable<WarehouseGoods> {
        let body = warehouseGoods;

        return this.http.put('/api/warehouseGoodss/', body)
            .pipe(
                map(response => new WarehouseGoods(response)),
                catchError(this.handleError)
            );
    }

    /**
     * Load a page (for paginated datatable) of WarehouseGoods using the passed
     * warehouseGoods as an example for the search by example facility.
     */
    getPage(warehouseGoods : WarehouseGoods, event : LazyLoadEvent) : Observable<PageResponse<WarehouseGoods>> {
        let req = new PageRequestByExample(warehouseGoods, event);
        let body = req;

        return this.http.post<PageResponse<any>>('/api/warehouseGoodss/page', body)
            .pipe(
                map(pr =>  new PageResponse<WarehouseGoods>(pr.totalPages, pr.totalElements, WarehouseGoods.toArray(pr.content))),
                catchError(this.handleError)
            );
    }

    /**
     * Performs a search by example on 1 attribute (defined on server side) and returns at most 10 results.
     * Used by WarehouseGoodsCompleteComponent.
     */
    complete(query : string) : Observable<WarehouseGoods[]> {
        let body = {'query': query, 'maxResults': 10};
        return this.http.post<any[]>('/api/warehouseGoodss/complete', body)
            .pipe(
                map(response => WarehouseGoods.toArray(response)),
                catchError(this.handleError)
            );
    }

    /**
     * Delete an WarehouseGoods by id.
     */
    delete(id : any) {
        return this.http.delete('/api/warehouseGoodss/' + id)
            .pipe(catchError(this.handleError));
    }

    // sample method from angular doc
    private handleError (error: HttpErrorResponse) {
        // TODO: seems we cannot use messageService from here...
        let errMsg = (error.message) ? error.message : 'Server error';
        console.error(errMsg);
        if (error.status === 401 ) {
            window.location.href = '/';
        }
        return Observable.throw(errMsg);
    }
}
