import {Component, OnInit, OnDestroy, Input, Output, EventEmitter} from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { SelectItem } from 'primeng/primeng';
import { MessageService} from '../../service/message.service';
import {WarehousePhoto} from './warehousePhoto';
import {WarehousePhotoService} from './warehousePhoto.service';
import {Warehouse} from '../warehouse/warehouse';

@Component({
    moduleId: module.id,
	templateUrl: 'warehousePhoto-detail.component.html',
	selector: 'warehousePhoto-detail',
})
export class WarehousePhotoDetailComponent implements OnInit, OnDestroy {
    warehousePhoto : WarehousePhoto;

    private params_subscription: any;


    @Input() sub : boolean = false;
    @Input() // used to pass the parent when creating a new WarehousePhoto
    set warehouse(warehouse : Warehouse) {
        this.warehousePhoto = new WarehousePhoto();
        this.warehousePhoto.warehouse = warehouse;
    }

    @Output() onSaveClicked = new EventEmitter<WarehousePhoto>();
    @Output() onCancelClicked = new EventEmitter();

    constructor(private route: ActivatedRoute, private router: Router, private messageService: MessageService, private warehousePhotoService: WarehousePhotoService) {
    }

    ngOnInit() {
        if (this.sub) {
            return;
        }

        this.params_subscription = this.route.params.subscribe(params => {
            let id = params['id'];
            console.log('ngOnInit for warehousePhoto-detail ' + id);

            if (id === 'new') {
                this.warehousePhoto = new WarehousePhoto();
            } else {
                this.warehousePhotoService.getWarehousePhoto(id)
                    .subscribe(warehousePhoto => {
                            this.warehousePhoto = warehousePhoto;
                        },
                        error =>  this.messageService.error('ngOnInit error', error)
                    );
            }
        });
    }

    ngOnDestroy() {
        if (!this.sub) {
            this.params_subscription.unsubscribe();
        }
    }

    gotoWarehouse() {
        this.router.navigate(['/warehouse', this.warehousePhoto.warehouse.id]);
    }

    clearWarehouse() {
        this.warehousePhoto.warehouse = null;
    }

    onSave() {
        this.warehousePhotoService.update(this.warehousePhoto).
            subscribe(
                warehousePhoto => {
                    this.warehousePhoto = warehousePhoto;
                    if (this.sub) {
                        this.onSaveClicked.emit(this.warehousePhoto);
                        this.messageService.info('Saved OK and msg emitted', 'Angular Rocks!')
                    } else {
                        this.messageService.info('Saved OK', 'Angular Rocks!')
                    }
                },
                error =>  this.messageService.error('Could not save', error)
            );
    }

    onCancel() {
        if (this.sub) {
            this.onCancelClicked.emit("cancel");
            this.messageService.info('Cancel clicked and msg emitted', 'Angular Rocks!')
        }
    }

}
